var contact_form_is_processing = false;
$(document).ready(function() {
	$('#contact-form-77 form').ajaxForm({
		'url': '/index.php/tools/packages/custom_contact_form/ajax_submit',
		'dataType': 'json',
		'data': {
			'bID': 77,
			'cID': 1,
			'ccm_token': '1438359684:777fc825856b9c8327352cf592e92fde'
		 },
		'beforeSubmit': function() {
			if (contact_form_is_processing) {
				return false; //prevent re-submission while waiting for response
			}
			contact_form_is_processing = true;
			$('#contact-form-77 .errors').hide();
			$('#contact-form-77 .errors .error-items').html('');
			$('#contact-form-77 .submit').hide();
			$('#contact-form-77 .processing').show();
		},
		'success': function(response) {
			contact_form_is_processing = false;
			if (response.success) {
				$('#contact-form-77 .processing').hide();
				$('#contact-form-77 form').fadeOut('', function() {
					$('#contact-form-77 .success').fadeIn();
					$('#contact-form-77 form').clearForm();
				});
			} else { //validation error
				var errorItems = '';
				for (var i = 0, len = response.errors.length; i < len; i++) {
					errorItems += '<li>' + response.errors[i] + '</li>';
				}
				$('#contact-form-77 .errors .error-items').html(errorItems);
				$('#contact-form-77 .errors').slideDown();
				$('#contact-form-77 .processing').hide();
				$('#contact-form-77 .submit').show();
				$('#contact-form-77 form input, #contact-form-77 form textarea').placeholder();
			}
			
			//scroll up to the success/error message
			$('body,html').animate({scrollTop : ($('#contact-form-77').offset().top - 50)}, 200);
		}
	});
	
	$('#contact-form-77 form input, #contact-form-77 form textarea').placeholder();
});
</script>

